if( ! function_exists( 'builder_menu_choices' ) ) {
	function builder_menu_choices() {
		$menus = wp_get_nav_menus();
		$menu_choices = array( 'none' => __('Please Choose', 'basr-core') );
		foreach( $menus as $menu ) {
			$menu_choices[$menu->slug] = $menu->name;
		}
		return $menu_choices;
	}
}

