function footer_get_copy() {
	$site_name = preg_replace( '/\..*/', '', $_SERVER['HTTP_HOST'] );
	$copy =  'Copyright © 2021 <a href="' . home_url('/') . '">' . strtoupper( $site_name ) . '</a>';
	return $copy;
}

function footer_get_siteName() {
	$site_name = preg_replace( '/\..*/', '', $_SERVER['HTTP_HOST'] );
	return strtoupper( $site_name );
}

function footer_builder_get_footer( $post_id ) {
	if( ! isset( $_GET['reset'] ) ) {
		$footer_cache = get_option( 'footer_builder_cache' );
		if( $footer_cache ) return $footer_cache;
	}
	$footer = fw()->extensions->get( 'footer-builder' );
	$footer_id = get_page_by_title( 'New Footer', OBJECT, 'footer_builder' );
	if( is_object( $footer_id ) && isset( $footer_id->ID ) ) {
		$post_id = $footer_id->ID;
	}
	ob_start();
	echo do_shortcode( $footer->render( $post_id ) );
	$footer_html = ob_get_clean();
	
	$footer_html = preg_replace_callback( '/{copyRight}|{payment}|{siteName}|{mail}|{sdt}|{address}/', function( $match ){
		$param = preg_replace('/\W/', '', $match[0]);
		switch ($param) {
			case 'siteName':
				return footer_get_siteName();
			case 'copyRight':
				return footer_get_copy();
			case 'payment':
				return shop_accept_payment();
			default:
				return footer_get_address($param);
				break;
		}
	}, $footer_html );

	update_option( 'footer_builder_cache', $footer_html );

	return $footer_html;
}

function footer_get_address( $param ) {
	$add   = origin_get_setting( 'site_address' );
	$phone = origin_get_setting( 'site_std' );
	$email = origin_get_setting( 'site_mail' );
	$html  = '';
	if( $email ) {
		$html .= '<a href="' . $email . '">' . $email . '</a>';
	}

	if( $phone ) {
		$html .= '</li><li>' . $phone;
	}

	if( $add ) {
		$html .= '</li><li>' . $add;
	}

	return $html;
}

