 *
 * @return bool|WP_Post
 */
function footer_builder_render_footer( $inputs ) {
	/**
	 * @var FW_Extension_Footer_Builder $footer
	 */
	ob_start();
	$footer = fw()->extensions->get( 'footer-builder' );
	$footer_html = $footer->render_footer($inputs);
	echo $footer_html;
	// $footer_html = preg_replace( '/{copyRight}/',  )
	return ob_get_clean( );
}

/**
 * @return array
 */
function footer_builder_get_all_footers() {
	$output = array();
	$args = array(
		'post_type'=> 'footer_builder',
		'order'    => 'ASC',
		'posts_per_page' => 100,
	);
	$the_query = new WP_Query( $args );
	if($the_query->have_posts() ) : ?>
		<?php while ( $the_query->have_posts() ) : ?>
			<?php $the_query->the_post(); ?>
			<?php $output[] = get_the_ID(); ?>
		<?php endwhile; ?>
	<?php endif; ?>
	<?php wp_reset_query();
	return $output;
}

/**
 * @return mixed|null
 */
function footer_builder_get_current_footer() {
	$footer_id = fw_get_db_post_option(get_the_ID(), 'footer');
	if( ! empty($footer_id) ) {
		return $footer_id[0];
	}
	$footer_id = fw_get_db_ext_settings_option('footer-builder', 'default_footer');
	if( ! empty($footer_id) ) {
		return $footer_id[0];
	}
	$all_footers = footer_builder_get_all_footers();
	if( ! empty($all_footers) ) {
		$footer_id = $all_footers[0];
		return $footer_id;
	}
	return;
}

add_filter( 'body_class', 'footer_builder_filter_body_class' );

function footer_builder_filter_body_class( $classes ) {
	$id =footer_builder_get_current_footer();
	$class = get_the_title( $id );
	$class = preg_replace('/\s+/', '-', $class );
	$classes[] = $class;

	return $classes;
}


